const body = $input.first().json.body || $input.first().json;
const raw_project = (body.project || 'default').toString().trim();
const project = raw_project.replace(/[^a-zA-Z0-9-]/g, '-').toLowerCase().replace(/^-+|-+$/g, '') || 'default';
const message = (body.message || '').toString().trim();

if (!message) {
  throw new Error('message parameter is required');
}

const workspace = `/home/node/workspace/${project}`;
return [{
  json: {
    project,
    message,
    workspace_path: workspace,
    state_path: `${workspace}/interview-state.json`,
    handoff_path: `${workspace}/handoffs/002-prd-interview.md`
  }
}];

const fs = require('fs');
const path = require('path');

const inputs = $('Code - Validate Inputs').first().json;
const workspacePath = inputs.workspace_path;
const handoffDir = path.join(workspacePath, 'handoffs');

fs.mkdirSync(handoffDir, { recursive: true });

return [{ json: { created: handoffDir } }];

const fs = require('fs');

const statePath = $('Code - Validate Inputs').first().json.state_path;

let stateJson = '{"messages":[],"turn":0}';
try {
  if (fs.existsSync(statePath)) {
    stateJson = fs.readFileSync(statePath, 'utf8');
  }
} catch(e) {
  // return empty state if any error
}

return [{ json: { stdout: stateJson } }];

const fs = require('fs');

function readFile(p) {
  try { return fs.readFileSync(p, 'utf8'); } catch(e) { return ''; }
}

const promptPath = '/home/node/prompts/prd-development/prd-interviewer.md';
const skill1Path = '/home/node/skills/prd/stakeholder-interview.md';
const skill2Path = '/home/node/skills/prd/requirements-engineering.md';

const prompt = readFile(promptPath);
const skill1 = readFile(skill1Path);
const skill2 = readFile(skill2Path);

const combined = [prompt, skill1, skill2].filter(Boolean).join('\n\n---\n\n');

return [{ json: { stdout: combined } }];

const inputs = $('Code - Validate Inputs').first().json;
const project = inputs.project;
const message = inputs.message;

// Parse conversation state
let state;
try {
  state = JSON.parse($('Execute Command - Read State').first().json.stdout || '{}');
} catch(e) {
  state = { messages: [], turn: 0 };
}
if (!Array.isArray(state.messages)) state.messages = [];
if (typeof state.turn !== 'number') state.turn = 0;

// Get system prompt (strip YAML frontmatter if present)
let systemPrompt = $('Execute Command - Read Prompt').first().json.stdout || '';
systemPrompt = systemPrompt.replace(/^---[\s\S]*?---\n/, '').trim();

// Handle start signal
const userContent = message === '__START__'
  ? 'Please begin the interview. Start by introducing yourself briefly and asking your first question.'
  : message;

// Build messages array
const messages = [
  { role: 'system', content: systemPrompt },
  ...state.messages,
  { role: 'user', content: userContent }
];

return [{
  json: {
    project,
    message: userContent,
    state,
    messages,
    state_path: inputs.state_path,
    handoff_path: inputs.handoff_path,
    model: 'qwen3.5:35b'
  }
}];

const ollamaResponse = $input.first().json;
const prevData = $('Code - Build Ollama Request').first().json;

const responseText = ollamaResponse.message?.content || ollamaResponse.choices?.[0]?.message?.content || '';
if (!responseText) throw new Error('Empty response from Ollama');

const isComplete = responseText.includes('INTERVIEW_COMPLETE');

// Update state: append user message + agent response
const state = prevData.state || { messages: [], turn: 0 };
state.messages.push({ role: 'user', content: prevData.message });
state.messages.push({ role: 'assistant', content: responseText });
state.turn = (state.turn || 0) + 1;

const stateJson = JSON.stringify(state, null, 2);
const stateB64 = Buffer.from(stateJson).toString('base64');

// Clean response for display (remove INTERVIEW_COMPLETE marker)
const displayResponse = responseText.replace(/\nINTERVIEW_COMPLETE\s*$/, '').replace(/INTERVIEW_COMPLETE\s*$/, '').trim();

return [{
  json: {
    project: prevData.project,
    response: displayResponse,
    full_response: responseText,
    is_complete: isComplete,
    turn: state.turn,
    state_path: prevData.state_path,
    handoff_path: prevData.handoff_path,
    state_b64: stateB64,
    messages: state.messages
  }
}];

const fs = require('fs');

const processData = $('Code - Process Response').first().json;
const stateJson = Buffer.from(processData.state_b64, 'base64').toString('utf8');

fs.writeFileSync(processData.state_path, stateJson, 'utf8');

return [{ json: { written: processData.state_path } }];

const extractionResponse = $input.first().json;
const processData = $('Code - Process Response').first().json;
const inputs = $('Code - Validate Inputs').first().json;

const extractedContent = extractionResponse.message?.content || extractionResponse.choices?.[0]?.message?.content || '';
if (!extractedContent.trim()) throw new Error('Extraction model returned empty content');

// Validate required sections
const required = [
  'Coverage Summary',
  'Problem Statement',
  'Target Users',
  'Core Functionality',
  'Scope',
  'Non-Functional',
  'Compliance',
  'Technical Constraints',
  'Timeline'
];
const missing = required.filter(s => !extractedContent.includes(s));
if (missing.length > 3) {
  throw new Error(`Handoff missing required sections: ${missing.join(', ')}`);
}

const now = new Date().toISOString();
const project = processData.project;

const handoff = `---
phase: prd-interview
completed: ${now}
agent: prd-interviewer
project: ${project}
entry_point: greenfield
compliance_applicable: false
compliance_frameworks: []
coverage_complete: true
---

${extractedContent.trim()}
`;

const handoffB64 = Buffer.from(handoff).toString('base64');

return [{
  json: {
    project,
    handoff_content: handoff,
    handoff_b64: handoffB64,
    handoff_path: inputs.handoff_path,
    missing_sections: missing
  }
}];

const fs = require('fs');
const path = require('path');

const formatData = $('Code - Format Handoff').first().json;
const handoffContent = Buffer.from(formatData.handoff_b64, 'base64').toString('utf8');

// Ensure directory exists
fs.mkdirSync(path.dirname(formatData.handoff_path), { recursive: true });
fs.writeFileSync(formatData.handoff_path, handoffContent, 'utf8');

return [{ json: { written: formatData.handoff_path, project: formatData.project, handoff_path: formatData.handoff_path } }];

const processData = $('Code - Process Response').first().json;
const messages = processData.messages || [];

// Build transcript text
const transcript = messages
  .map(m => m.role.toUpperCase() + ': ' + m.content)
  .join('

');

const extractionMessages = [
  {
    role: 'system',
    content: 'You are a requirements extraction specialist. Extract all project requirements from the PRD interview transcript into a structured markdown document. Output ONLY the structured document — no preamble, no commentary, no meta-statements like "Here is the extracted requirements".'
  },
  {
    role: 'user',
    content: `Interview transcript:

${transcript}

Extract all requirements into this exact structure:

## Coverage Summary
(Table showing which of the 8 coverage areas were addressed — mark each as ✓ Covered, N/A, or Incomplete)

## Problem Statement & Success Criteria
(What problem this solves, who is affected, measurable success criteria)

## Target Users
(Primary and secondary users, their goals, skills, constraints)

## Core Functionality
(Must-have features and user actions the system must support)

## Scope & Boundaries
(What is in scope for MVP, what is out of scope, what is deferred)

## Non-Functional Requirements
(Performance targets, security requirements, availability, data volumes)

## Compliance
(Applicable compliance frameworks or "None identified")

## Technical Constraints
(Existing tech stack, language/framework requirements, deployment environment)

## Timeline & Resources
(Target delivery timeline, team size, budget constraints)

Be specific. Use only information that was actually stated in the interview.`
  }
];

return [{
  json: {
    extraction_messages: extractionMessages,
    project: processData.project
  }
}];
